local function nothing () end local alltalk = ulx.command( "Chat", "ulx alltalk", nothing, "!alltalk" ) alltalk:addParam{ type=ULib.cmds.PlayersArg, ULib.cmds.optional } alltalk:addParam{ type=ULib.cmds.BoolArg, invisible=true } alltalk:defaultAccess( ULib.ACCESS_ADMIN ) alltalk:help( "Grants/revokes almighty ears to/from target." )  alltalk:setOpposite( "ulx unalltalk", {_, _, true}, "!unalltalk" ) hook.Add("PlayerCanHearPlayersVoice", "ULXAlltalk", function( listener, speaker ) if listener.ULXAlltalk or speaker.ULXAlltalk then return true end end)